dependencyManagement.dependencies:
{#for dependency in dependencies}

  {dependency.identifier.groupId}
  {dependency.identifier.artifactId}
  {dependency.version}
  {#if dependency.identifier.type != "jar"}
  {dependency.identifier.type}
  {/if}
  {#if dependency.scope != "compile"}
  {dependency.scope}
  {/if}
  {#if dependency.identifier.classifier.isPresent()}
  {dependency.identifier.classifier.get()}
  {/if}
  {#if dependency.optional}
  true
  {/if}

{/for}
